select hs.code_2 AS hs_code2 , hs.title_2 AS hs_title2 , hs.code_4 AS hs_code4 , hs.title_4 AS hs_title4 , hs.code_6 AS hs_code6 , hs.title_6 AS hs_title6 , hs.code_10 AS hs_code10 , hs.title_10 AS hs_title10 from hs_depth as hs
AND CODE_2 = #{code}
AND CODE_4 = #{code}
AND CODE_6 = #{code}
AND CODE_10 = #{code}

select mti.code_1 AS mti_code1, mti.title_1 AS mti_title1
,mti.code_2 AS mti_code2, mti.title_2 AS mti_title2
,mti.code_3 AS mti_code3, mti.title_3 AS mti_title3
,mti.code_6 AS mti_code6, mti.title_6 AS mti_title6
from mti_depth as mti



			AND CODE_1 = #{code}


			AND CODE_2 = #{code}


			AND CODE_3 = #{code}


			AND CODE_4 = #{code}

select sitc.code_2 AS sitc_code2, sitc.title_2 AS sitc_title2
,sitc.code_3 AS sitc_code3, sitc.title_3 AS sitc_title3
,sitc.code_5 AS sitc_code5, sitc.title_5 AS sitc_title5
from sitc_depth as sitc



			AND CODE_2 = #{code}


			AND CODE_5 = #{code}


			AND CODE_5 = #{code}

select icio.code AS icio_code1, icio.title AS icio_title1
from code_icio as icio


		code = #{code}

SELECT code_1 AS isic_code1, title_1 AS isic_title1 , code_2 AS isic_code2, title_2 AS isic_title2 FROM isic_depth
AND code_1 = #{code}
AND code_1 = substr(#{code},1,1) AND code_2 = substr(#{code},2,2)

SELECT ksic_1.code AS ksic_code1 ,ksic_1.title AS ksic_title1, ksic_2.code AS ksic_code2, ksic_2.title AS ksic_title2 FROM code_ksic AS ksic_1 INNER JOIN code_ksic AS ksic_2 ON (ksic_1.code = ksic_2.catagory)
AND ksic_1.code = #{code}
AND ksic_1.code = substr(#{code},1,1) AND ksic_2.code = substr(#{code},2,2)

select hs.code_2 AS hs_code2, hs.title_2 AS hs_title2
, hs.code_4 AS hs_code4, hs.title_4 AS hs_title4
, hs.code_6 AS hs_code6, hs.title_6 AS hs_title6
, hs.code_10 AS hs_code10, hs.title_10 AS hs_title10
FROM code_map AS map INNER JOIN hs_depth AS hs ON (map.hs = hs.code_10)


		INNER JOIN mti_depth AS mti ON (map.mti = mti.code_6)


				mti.code_1 = #{targetCode}
				mti.code_2 = #{targetCode}
				mti.code_3 = #{targetCode}
				mti.code_6 = #{targetCode}




		INNER JOIN sitc_depth AS sitc ON (map.sitc = sitc.code_5)


				sitc.code_2 = #{targetCode}
				sitc.code_3 = #{targetCode}
				sitc.code_5 = #{targetCode}




		INNER JOIN code_icio AS icio ON (map.icio = icio.code)

			icio.code = #{targetCode}



		INNER JOIN isic_depth AS isic ON (map.isic = isic.code_2)


					isic.code_1 = #{targetCode}
					isic.code_2 = #{targetCode}





	and hs.code_2 = #{sourceCode}
	and hs.code_4 = #{sourceCode}
	and hs.code_6 = #{sourceCode}

